import block_footer from "../Units/Block_Footer";
import block_nav from "../Units/BlockNav";
import block_nav_vip from "../Units/Block_Nav_Vip";
import { Promise } from 'q';
import { HTTP } from "../../http-common";
import { router } from "../../routers";
import datepicker from 'vuejs-datepicker';
import { socket_bus } from "../../main";
export default {
  components: {
    block_footer: block_footer,
    block_nav: block_nav,
    datepicker: datepicker,
    block_nav_vip: block_nav_vip
  },
  data: function data() {
    return {
      format: 'dd/MM/yyyy',
      id_brand: '',
      events: [],
      brand: {
        id: "",
        name: "",
        image_source: {
          logo: "",
          thumbnail: "",
          image_intro: "",
          main_banner: ""
        },
        id_category: "",
        id_customer: "",
        introduce: "",
        location: [],
        phone_number: "",
        terms: [],
        website: "",
        priority: 0,
        price: 0,
        level: 1,
        email: "",
        feauture: "",
        event: []
      },
      joining_demands: [],
      new_joining_request: {
        id_brand: "",
        sex: "",
        name: "",
        email: "",
        location: "",
        budget: {
          from: 0,
          to: 0
        },
        store_front: false,
        open_time: new Date(Date.now() + 7 * 60 * 60 * 1000),
        phone_number: "",
        zalo: "",
        time_contact: "0",
        message: ""
      },
      list_city: [],
      city_show: false,
      is_hot_label: false,
      city_choose: {
        _id: '',
        name: '-- Select City --'
      },
      customer: {},
      wish_list_current: [],
      is_wish_list: false,
      list_icon: [],
      list_image_vip: [],
      i: 0,
      position: 0,
      is_owner: true,
      is_vip: false
    };
  },
  methods: {
    handle_check_wish_list: function handle_check_wish_list(id_brand) {
      return this.wish_list_current.indexOf(id_brand) == -1 ? false : true;
    },
    handle_click: function handle_click(action) {
      var _this = this;

      var list_brand_element = document.getElementById('list-menu');
      var count = document.querySelectorAll('#list-menu .menu-item').length * 270 / 1080;
      var marginLeft = 1080;

      switch (action) {
        case 'NEXT':
          this.i = this.i == count - 1 ? 0 : this.i + 1;
          break;

        case 'PREV':
          this.i = this.i == 0 ? 0 : this.i - 1;
          break;
      }

      setTimeout(function () {
        list_brand_element.style.marginLeft = "-" + marginLeft * _this.i + "px";
      }, 200);
    },
    handle_click_action: function handle_click_action(action) {
      switch (action) {
        case 'NEXT':
          this.position = this.position < 2 ? this.position + 1 : 0;
          break;

        case 'PREV':
          this.position = this.position > 2 ? this.position - 1 : 2;
          break;
      }

      document.getElementById('bgvip').style.backgroundImage = "url('".concat(this.list_image_vip[this.position], "')");
    },
    handle_get_info_brand: function handle_get_info_brand(id_brand) {
      var _this2 = this;

      return new Promise(function (resolve, reject) {
        HTTP.get('/brands/' + id_brand).then(function (result) {
          resolve(result);

          if (result.data.status == 200) {
            _this2.brand = result.data.data;
            _this2.events = _this2.brand.events;

            _this2.handle_get_joining_demands(_this2.brand.id_category);

            _this2.is_hot_label = _this2.brand.hotlabel;
            _this2.is_vip = _this2.brand.level == 'NORMAL' ? false : true;
            _this2.list_image_vip = _this2.brand.image_source.main_banner;

            if (document.getElementById('bgvip')) {
              document.getElementById('bgvip').style.backgroundImage = "url('".concat(_this2.list_image_vip[0], "')");
              console.log("url('".concat(_this2.list_image_vip[0], "')"));
            }
          } else {
            _this2.$router.push('/');
          }
        }).catch(function (error) {
          reject(error);
          console.log('Error: ' + error);
        });
      });
    },
    handle_return_date_string: function handle_return_date_string(date) {
      return new Date(date).getDate() + '/' + (new Date(date).getMonth() + 1) + '/' + new Date(date).getFullYear();
    },
    handle_add_wish_list: function handle_add_wish_list(brand) {
      var _this3 = this;

      var brand_wish = document.getElementById(brand.id);
      var element_first = brand_wish.children[0];

      if (localStorage.getItem('access_token')) {
        if (element_first.checked) {
          HTTP.patch("/wish/remove-user/brand=".concat(brand.id, "&customer=").concat(this.customer.id)).then(function (result) {
            alert(result.data.message);

            if (result.data.status == 200) {
              window.location.reload();
              element_first.checked = false;
              brand_wish.children[1].style.color = '#F3A8B7';
            }
          }).catch(function (error) {
            console.log('Error: ' + error);
          });
        } else if (!element_first.checked) {
          HTTP.patch('/wish/' + brand.id, {
            id_customer: this.customer.id
          }).then(function (result) {
            setTimeout(function () {
              alert(result.data.message);
            }, 100);

            if (result.data.status == 200) {
              window.location.reload();
              element_first.checked = true;
              _this3.brand = brand;
            }
          }).catch(function (error) {
            console.log('Error: ' + error);
          });
        }
      } else {
        router.push('/login');
      }
    },
    handle_get_joining_demands: function handle_get_joining_demands(category) {
      var _this4 = this;

      return new Promise(function (resolve, reject) {
        HTTP.get('joining-demand/category-type=' + category).then(function (result) {
          resolve(result);
          _this4.joining_demands = result.data.status == 200 ? result.data.data : [];
        }).catch(function (error) {
          reject(error);
          console.log(error);
        });
      });
    },
    handle_select_city: function handle_select_city(i) {
      this.city_choose = this.list_city[i];
      this.city_show = false;
      this.new_joining_request.location = this.new_joining_request.location.length == 0 ? this.new_joining_request.location += this.city_choose.name : this.new_joining_request.location += ', ' + this.city_choose.name;
    },
    changeHandler: function changeHandler() {
      this.new_joining_request.time_contact = this.timer_call.HH + ':' + this.timer_call.mm;
    },
    handle_return_string_money: function handle_return_string_money(text) {
      var length = text.length;

      if (length > 6) {
        return text.substr(0, text.length - 6);
      } else if (length == 6) {
        return '0.' + text.substr(0, 1);
      }
    },
    handle_toogle: function handle_toogle($event) {
      this.city_show = !this.city_show;
      $event.stopPropagation();
    },
    handle_post_joining_request: function handle_post_joining_request() {
      var _this5 = this;

      this.$validator.validateAll();

      if (!this.errors.any()) {
        this.new_joining_request.id_brand = this.brand.id;
        return new Promise(function (resolve, reject) {
          HTTP.post('/joining-request', _this5.new_joining_request).then(function (result) {
            resolve(result);

            if (result.data.status == 201) {
              socket_bus.$emit('SHOW_MESSAGE', 'Post your request Success');
              setTimeout(function () {
                window.location.reload();
              }, 3000);
            } else {
              socket_bus.$emit('SHOW_MESSAGE', 'Post your request Fail');
            }
          }).catch(function (error) {
            reject(error);
            console.log(error);
          });
        });
      } else {
        alert('Post Your Request Brand FAIL');
      }
    },
    handle_scroll_to_top: function handle_scroll_to_top(item) {
      var online_inquery = document.getElementById(item);
      var startTime = null;
      var duration = 1000;
      var start_position = parseFloat(window.pageYOffset);
      var target_position = parseFloat(online_inquery.offsetTop);
      var distance = target_position - 10;

      function animation(current_time) {
        if (startTime === null) startTime = current_time;
        var timeElapsed = current_time - startTime;
        var run = ease(timeElapsed, start_position, distance, duration);
        window.scrollTo(0, run);

        if (timeElapsed < duration) {
          requestAnimationFrame(animation);
        }
      }

      function ease(t, b, c, d) {
        t /= d / 2;
        if (t < 1) return c / 2 * t * t + b;
        t--;
        return -c / 2 * (t * (t - 2) - 1) + b;
      }

      requestAnimationFrame(animation);
    },
    handle_scroll_to_top_non_vip: function handle_scroll_to_top_non_vip(item, button) {
      var online_inquery = document.getElementById(item);
      var btn_click = document.getElementById(button);
      var startTime = null;
      var duration = 1000;
      var start_position = parseFloat(btn_click.offsetTop);
      var target_position = parseFloat(online_inquery.offsetTop);
      var distance = target_position - start_position - 100;

      function animation(current_time) {
        if (startTime === null) startTime = current_time;
        var timeElapsed = current_time - startTime;
        var run = ease(timeElapsed, start_position, distance, duration);
        window.scrollTo(0, run);

        if (timeElapsed < duration) {
          requestAnimationFrame(animation);
        }
      }

      function ease(t, b, c, d) {
        t /= d / 2;
        if (t < 1) return c / 2 * t * t + b;
        t--;
        return -c / 2 * (t * (t - 2) - 1) + b;
      }

      requestAnimationFrame(animation);
    },
    handle_get_wish_list: function handle_get_wish_list(id_customer) {
      var _this6 = this;

      HTTP.get('/customers/' + id_customer).then(function (result) {
        localStorage.removeItem('customer');
        localStorage.setItem('customer', JSON.stringify(result.data));
        _this6.customer = result.data;
        _this6.wish_list_current = result.data.wish_list;

        _this6.wish_list_current.map(function (item) {
          _this6.is_wish_list = item == _this6.id_brand ? true : false;
        });
      }).catch(function (error) {
        console.log(error);
      });
    }
  },
  mounted: function mounted() {
    var _this7 = this;

    this.$nextTick(function () {
      window.addEventListener('click', function () {
        if (_this7.city_show) {
          _this7.city_show = false;
        }
      });
    });
  },
  created: function created() {
    var _this8 = this;

    this.id_brand = this.$route.params.id;

    if (JSON.parse(localStorage.getItem('customer'))) {
      this.customer = JSON.parse(localStorage.getItem('customer'));
      this.handle_get_wish_list(this.customer.id);
    }

    this.handle_get_info_brand(this.$route.params.id);
    this.$store.dispatch('get_cities').then(function (result) {
      _this8.list_city = result.data;
    });
    this.list_icon = ['Equipments.png', 'Need-Crowd.png', 'Low-Risk.png', 'Free-Franchise-Fee.png', 'Have-Decorated.png', 'Free-Signing.png', 'Exemption.png', 'Education.png', 'Free-Margin.png'];
  }
};
