        socket_bus.$emit('SHOW_MESSAGE', {
          message: 'Main Banner Only 2 Picture'
        });
        this.main_banner = [];
        e.target.value = '';
      } else if (this.image_intro.length > 5) {
        socket_bus.$emit('SHOW_MESSAGE', {
          message: 'Image Intro Only 5 Picture'
        });
        this.image_intro = [];
        e.target.value = '';
      } else if (this.menu.length > 5) {
        socket_bus.$emit('SHOW_MESSAGE', {
          message: 'Menu Only 5 Picture'
        });
        this.menu = [];
        e.target.value = '';
      }

