                message: 'Size image upload is over rules'
              });
              _this2.logo = null;
              e.target.value = '';
            } else {
              _this2.url_preview.push(URL.createObjectURL(files[0]));
            }
          };

          break;

        case 'THUMBNAIL':
          image.src = _URL.createObjectURL(files[0]);

          image.onload = function () {
            if (image.width != 870 || image.height != 270) {
              socket_bus.$emit('SHOW_MESSAGE', {
                message: 'Size image upload is over rules'
              });
              _this2.thumbnail = null;
              e.target.value = '';
            } else {
              _this2.url_preview.push(URL.createObjectURL(files[0]));
            }
          };

          break;

        case 'MAIN_BANNER':
          for (var i = 0; i < files.length; i++) {
            image.src = _URL.createObjectURL(files[i]);

            image.onload = function () {
              if (image.width != 960 || image.height != 720) {
                socket_bus.$emit('SHOW_MESSAGE', {
                  message: 'Size image upload is over rules'
                });
                _this2.main_banner = null;
                e.target.value = '';
                _this2.url_preview = null;
