import block_nav from "../Units/BlockNav";
import block_alert from "../Units/Block_Alert";
import Block_Banner_Bottom from "../Units/Block_Banner_Bottom";
import block_footer from "../Units/Block_Footer";
import datepicker from 'vuejs-datepicker';
import { HTTP } from "../../http-common";
import { router } from "../../routers";
import { socket_bus } from "../../main";
import VueRecaptcha from 'vue-recaptcha';
import VueNumeric from 'vue-numeric';
export default {
  components: {
    block_nav: block_nav,
    block_footer: block_footer,
    Block_Banner_Bottom: Block_Banner_Bottom,
    datepicker: datepicker,
    VueNumeric: VueNumeric,
    VueRecaptcha: VueRecaptcha,
    block_alert: block_alert
  },
  data: function data() {
    return {
      page: 1,
      format: 'dd/MM/yyyy',
      category_show: false,
      is_Input_Active: false,
      city_show: false,
      current_tab: 'ALL',
      is_login: false,
      list_joining_demand: [],
      list_category: [],
      category_choose: {
        _id: '',
        name: 'Choose Category'
      },
      list_city: [],
      city_choose: {
        _id: '',
        name: '-- Select City --'
      },
      new_joining_post: {
        category: '',
        budget: {
          from: 0,
          to: 0
        },
        location: [],
        store_front: false,
        time_open: new Date(Date.now() + 7 * 60 * 60 * 1000),
        name: '',
        email: '',
        phone_number: '',
        zalo: '',
        time_call: '0',
        id_customer: '',
        message: '',
        created_on: new Date(Date.now() + 7 * 60 * 60 * 1000)
      },
      disable_date: {
        to: new Date(),
        ranges: []
      },
      token_recaptcha: null,
      email: '',
      password: '',
      image_banner: '',
      name_picture: '',
      category_popular: [],
      category_vn: ['BBQ-vi.jpg', 'Brunch-vi.jpg', 'Dessert-vi.jpg', 'Drink-vi.jpg', 'Foriegn-vi.jpg', 'Hotpot-vi.jpg', 'Vietnamese-vi.jpg'],
      category_en: ['BBQ-eng.jpg', 'Brunch-eng.jpg', 'Dessert-eng.jpg', 'Drink-eng.jpg', 'Foriegn-eng.jpg', 'Hotpot-eng.jpg', 'Vietnamese-eng.jpg']
    };
  },
  methods: {
    handle_route: function handle_route(index) {
      var route = '';

      switch (index) {
        case 0:
          route = 'BBQ';
          break;

        case 1:
          route = 'Brunch';
          break;

        case 2:
          route = 'Dessert';
          break;

        case 3:
          route = 'Drink';
          break;

        case 4:
          route = 'Foriegn Foods';
          break;

        case 5:
          route = 'Hot Pot';
          break;

        case 6:
          route = 'Vietnamese Foods';
          break;
      }

      router.push("/category=".concat(route));
      window.location.reload();
    },
    handle_verify: function handle_verify(response) {
      this.token_recaptcha = response;
    },
    handle_expired: function handle_expired() {
      this.$refs.recaptcha.reset();
    },
    handle_replace_phone: function handle_replace_phone(text, start, length) {
      var length_cut = (text.length - length) / 2;
      return text.substr(start, length_cut) + '*'.repeat(length) + text.substr(length + length_cut, length_cut);
    },
    handle_replace_string: function handle_replace_string(text, start, length) {
      return text.substr(start, length) + '*'.repeat(7);
    },
    handle_return_string_money: function handle_return_string_money(text) {
      var length = text.length;

      if (length > 6) {
        return text.substr(0, text.length - 6);
      } else if (length == 6) {
        return '0.' + text.substr(0, 1);
      }
    },
    handle_get_joining: function handle_get_joining(event) {
      switch (event) {
        case 'OLD':
          this.page = this.list_joining_demand.length < 1 ? 1 : this.page += 1;
          break;

        case 'NEW':
          this.page = this.page == 1 ? 1 : this.page -= 1;
          break;
      }

      this.handle_get_joining_budget(this.page);
    },
    handle_select_category: function handle_select_category(i) {
      this.category_show = false;
      this.category_choose = this.list_category[i];
      this.new_joining_post.category = this.category_choose.name;
    },
    handle_select_city: function handle_select_city(i) {
      this.city_choose = this.list_city[i];
      this.city_show = false;
      this.new_joining_post.location = this.new_joining_post.location.length == 0 ? this.new_joining_post.location += this.city_choose.name : this.new_joining_post.location += ', ' + this.city_choose.name;
    },
    handle_toogle: function handle_toogle($event) {
      this.city_show = !this.city_show;
      $event.stopPropagation();
    },
    handle_toogle_category: function handle_toogle_category($event) {
      this.category_show = !this.category_show;
      $event.stopPropagation();
    },
    handle_login: function handle_login() {
      if (this.token_recaptcha != null) {
        this.$validator.validateAll();

        if (!this.errors.any()) {
          var info_login = {
            email: this.email,
            password: this.password
          };
          this.$store.dispatch('handle_sign_in', info_login).then(function (result) {
            if (result.data.status == 200) {
              socket_bus.$emit('SHOW_ALERT', {
                is_show: true,
                message: 'Login Account Success',
                is_redirect: false,
                brand: {
                  name: 'LOGIN SUCCESS'
                }
              });
            } else if (result.data.status == 403) {
              socket_bus.$emit('SHOW_ALERT', {
                is_show: true,
                message: 'Login Account NOT ACTIVE',
                is_redirect: false,
                brand: {
                  name: 'LOGIN FAIL'
                }
              });
            } else if (result.data.status == 503) {
              socket_bus.$emit('SHOW_ALERT', {
                is_show: true,
                message: 'Login Account LOCK',
                is_redirect: false,
                brand: {
                  name: 'LOGIN FAIL'
                }
              });
            } else if (result.data.status == 401) {
              socket_bus.$emit('SHOW_ALERT', {
                is_show: true,
                message: result.data.error,
                is_redirect: false,
                brand: {
                  name: 'LOGIN FAIL'
                }
              });
            } else {
              socket_bus.$emit('SHOW_ALERT', {
                is_show: true,
                message: result.data.error,
                is_redirect: false,
                brand: {
                  name: 'LOGIN FAIL'
                }
              });
            }
          });
        }
      }
    },
    handle_post_request: function handle_post_request() {
      var _this = this;

      if (JSON.parse(localStorage.getItem('customer'))) {
        var customer = JSON.parse(localStorage.getItem('customer'));
        this.new_joining_post.id_customer = customer.id;
        this.$validator.validateAll();

        if (!this.errors.any()) {
          return new Promise(function (resolve, reject) {
            HTTP.post('/joining-demand/', _this.new_joining_post).then(function (result) {
              resolve(result);

              if (result.data.status == 201) {
                alert('Post your demand success');
                window.location.reload();
              } else {
                alert('Post your demand fail');
              }
            }).catch(function (error) {
              reject(error);
              console.log('Error: ' + error);
            });
          });
        }
      } else {
        this.is_login = true;
      }
    },
    handle_get_joining_budget: function handle_get_joining_budget(page) {
      var _this2 = this;

      return new Promise(function (resolve, reject) {
        HTTP.get('/joining-demand/page=' + page).then(function (result) {
          resolve(result);
          _this2.list_joining_demand = result.data.status == 200 ? result.data.data : [];
        }).catch(function (error) {
          reject(error);
          console.log(error);
        });
      });
    },
    handle_get_name_image: function handle_get_name_image(link) {
      return String(link).split('/')[2].split('.')[0];
    },
    handle_get_image_advertisement: function handle_get_image_advertisement() {
      var _this3 = this;

      this.$store.dispatch('handle_get_link_image').then(function (result) {
        _this3.image_banner = result.data[0].header;
        _this3.name_picture = _this3.handle_get_name_image(_this3.image_banner);
      });
    },
    handle_choose_tab: function handle_choose_tab(tab) {
      switch (tab) {
        case 'ALL':
          this.handle_get_joining_budget(this.page);
          this.current_tab = 'ALL';
          break;

        case 'DOMESTIC':
          this.current_tab = 'DOMESTIC';
          this.list_joining_demand = this.list_joining_demand.filter(function (item) {
            return item.location.indexOf('oversea') != -1;
          });
          break;

        case 'OVERSEA':
          this.current_tab = 'OVERSEA';
          this.list_joining_demand = this.list_joining_demand.filter(function (item) {
            return item.location.indexOf('oversea') == 1;
          });
          break;
      }
    }
  },
  mounted: function mounted() {
    var _this4 = this;

    this.$nextTick(function () {
      window.addEventListener('click', function () {
        if (_this4.city_show) {
          _this4.city_show = false;
        }

        if (_this4.category_show) {
          _this4.category_show = false;
        }
      });
    });
  },
  created: function created() {
    var _this5 = this;

    this.city_show = false;
    this.$store.dispatch('get_cities').then(function (result) {
      _this5.list_city = result.data;
    });
    this.$store.dispatch('get_categories').then(function (result) {
      _this5.list_category = result.data;
    });
    this.handle_get_joining_budget(this.page);
    this.category_popular = localStorage.getItem('lang') == 'vn' ? this.category_vn : this.category_en;
    socket_bus.$on('CHANGE_LANG', function () {
      window.location.reload();
    });
    socket_bus.$on('CLOSE_BOX', function (result) {
      _this5.is_login = false;
    });
  }
};
