import { HTTP } from "../../http-common";
export default {
  data: function data() {
    return {
      image_banner: {},
      brand_left: '',
      brand_center: '',
      brand_right_top: '',
      brand_right_bottom: '',
      brand_wide: ''
    };
  },
  methods: {
    handle_get_name_image: function handle_get_name_image(link) {
      return String(link).split('/')[2].split('.')[0];
    },
    handle_get_image_advertisement: function handle_get_image_advertisement() {
      var _this = this;

      this.$store.dispatch('handle_get_link_image').then(function (result) {
        _this.image_banner = result.data[0].exhibition;

        if (_this.image_banner.left.split('/')[1] == 'advertisement') {
          var id_brand = _this.image_banner.left.split('/')[2];

          _this.handle_get_info_brand_in_image(id_brand).then(function (result) {
            _this.brand_left = result.status == 200 ? "".concat(result.data.id_category, "/").concat(result.data.name_vietnamese) : '';
          });
        } else {
          _this.brand_left = '';
        }

        if (_this.image_banner.center.split('/')[1] == 'advertisement') {
          var id_brand_center = _this.image_banner.center.split('/')[2];

          _this.handle_get_info_brand_in_image(id_brand_center).then(function (result) {
            _this.brand_center = result.status == 200 ? "".concat(result.data.id_category, "/").concat(result.data.name_vietnamese) : '';
          });
        } else {
          _this.brand_center = '';
        }

        if (_this.image_banner.right_top.split('/')[1] == 'advertisement') {
          var id_brand_right_top = _this.image_banner.right_top.split('/')[2];

          _this.handle_get_info_brand_in_image(id_brand_right_top).then(function (result) {
            _this.brand_right_top = result.status == 200 ? "".concat(result.data.id_category, "/").concat(result.data.name_vietnamese) : '';
          });
        } else {
          _this.brand_right_top = '';
        }

        if (_this.image_banner.right_bottom.split('/')[1] == 'advertisement') {
          var id_brand_right_bottom = _this.image_banner.right_bottom.split('/')[2];

          _this.handle_get_info_brand_in_image(id_brand_right_bottom).then(function (result) {
            _this.brand_right_bottom = result.status == 200 ? "".concat(result.data.id_category, "/").concat(result.data.name_vietnamese) : '';
          });
        } else {
          _this.brand_right_bottom = '';
        }

        if (_this.image_banner.wide.split('/')[1] == 'advertisement') {
          var id_brand_side_bar = _this.image_banner.wide.split('/')[2];

          _this.handle_get_info_brand_in_image(id_brand_side_bar).then(function (result) {
            _this.brand_wide = result.status == 200 ? "".concat(result.data.id_category, "/").concat(result.data.name_vietnamese) : '';
          });
        } else {
          _this.brand_wide = '';
        }
      });
    },
    handle_get_info_brand_in_image: function handle_get_info_brand_in_image(id_brand) {
      return new Promise(function (resolve, reject) {
        HTTP.get("/brands/".concat(id_brand)).then(function (result_brand) {
          resolve(result_brand.data);
          return result_brand.data.status == 200 ? "".concat(result_brand.data.data.id_category, "/").concat(result_brand.data.data.name_vietnamese) : '';
        }).catch(function (error) {
          reject(error);
          console.log("Error get info brand ".concat(JSON.stringify(error)));
        });
      });
    }
  },
  created: function created() {
    this.handle_get_image_advertisement();
  }
};
