import Vue from 'vue'
import Vuex from 'vuex'
import {router} from './routers';
import App from './App.vue';
import {store} from './store';
import VeeValidate from 'vee-validate';
import VueMask from 'v-mask'
import VueTimepicker from 'vuejs-timepicker'
import VueNumeric from 'vue-numeric'
import i18n from '@/lang/i18n.js'
// Components
Vue.config.productionTip = false
Vue.use(Vuex)
Vue.use(VueMask);
Vue.use(VueTimepicker);
Vue.use(VueNumeric)

export const socket_bus = new Vue();
const dictionary = {
    en: {
        custom: {
            name: {
                required:'Name is required'
            },
            email: {
                required:'Email is required',
                regex: 'Email is not correct format'
            },
            phone_number: {
                required:'Phone number is required',
                regex: 'Phone number is not correct'
            },
            address: {
                required:'Address is required'
            },
            password: {
                required:'Password is required',
                min: (password, params) => `Password is least ${params[0]} characters`
            }, 
            re_password: {
                required:'Re-Password is required',
                confirmed: 'Password is not match'
            },
            logo : {
                required: 'Logo is required'
            },
            thumbnail : {
                required: 'Thumbnail is required'
            },
            price : {
                required: 'Price is required',
                regex: 'Price is only numberic'
            },
            name_category: {
                required: 'Name category is required'
            },
            description: {
                required: 'Description is required'
            }
        }
    }
}
Vue.use(VeeValidate, {dictionary: dictionary,  fieldsBagName: 'vvFields'})
new Vue({
    i18n,
    store,
    router,
    render: h => h(App),
}).$mount('#app')

window.onload = () => {

    if(document.querySelectorAll("#bannerimage img").length > 0 && document.getElementById("bannerimage")) {
        var list = document.querySelectorAll("#bannerimage img");
        var i = 0;
        var images = document.getElementById("bannerimage");
        images.style.marginLeft = 0
        var list_dot = document.querySelectorAll(".circle-mini");
        list_dot[0].classList.add("choose")

        setInterval(() => {
            for(var j = 0; j < list_dot.length ; j++) {
                list_dot[j].classList.remove("choose");
                list[i].style.opacity = 0.2
            }

            i = (i >= list.length - 1) ? 0 : i + 1;
            list[i].style.opacity = 1
            var marginLeft = i != 0 ? i * list[i].width : 0;
            images.style.transform = `translate(-${marginLeft}px)`
            list_dot[i].classList.add("choose");

        }, 4000);
    }


    window.onscroll = function() {
        // console.log(window.pageYOffset)
        if(document.getElementById('nav-bar-state')) {
            var nav_bar_state = document.getElementById('nav-bar-state');
            if( window.pageYOffset > nav_bar_state.offsetTop) {
                nav_bar_state.classList.add('fix-top-state')
            } else {
                nav_bar_state.classList.remove('fix-top-state')
            }
        }

        if(document.getElementById('nav-bar')) {
            var navbar = document.getElementById('nav-bar');
            if( window.pageYOffset > navbar.offsetTop) {
                navbar.classList.add('fix-top-fix');
            } else {
                navbar.classList.remove('fix-top-fix');
            }
        }


        if(document.getElementById('nav-bar-joining')) {
            var navbarjoining = document.getElementById('nav-bar-joining');
            if( window.pageYOffset > navbarjoining.offsetTop) {
                navbarjoining.classList.add('fix-top-fix');
                navbarjoining.style.top = "36px"
                navbarjoining.classList.add('opacity-35');
            } else {
                navbarjoining.classList.remove('fix-top-fix');
                navbarjoining.classList.remove('opacity-35');
                navbarjoining.style.top = "0px"
            }
        } 

        /* BUTTON SCROLL */
        if(document.getElementsByClassName('box-scroll').length != 0) {
            var btn_scroll = document.getElementsByClassName('box-scroll');
            if( window.pageYOffset >= 20) {
                btn_scroll[0].style.display = 'flex'
            } else {
                btn_scroll[0].style.display = 'none'
            }
        }

    }

    if(document.getElementById("box-scroll")) {
        var btn_scroll = document.getElementById('box-scroll')
        btn_scroll.addEventListener("click", function() {
            window.scroll({
                top: 0,
                behavior: 'smooth'
            });

        });
    }
}
