import {HTTP} from '../../../../http-common'
import detail_brand from './Detail-Brand'
import { socket_bus } from '../../../../main'

export default {
    components: { detail_brand,
    },
    data() {
        return {
            customer: {},
            brands: [],
            brand_selected: null,
            is_add: false,
            is_change_image: false,
            images: '',
            thumbnail: '',
            main_banner: '',
            image_intro: '',
            menu: '',
            logo:'',
            is_logo: '',
            qr_code_image: '',
            url_preview: null,
        }
    },
    methods: {
        handle_translate(name) {
            var result = ''
            switch(name) {
                case 'Drink':
                    result = this.$t('drink')
                    break;
                case 'Dessert':
                    result = this.$t('dessert')
                    break;
                case 'Vietnamese Foods':
                    result = this.$t('vn_food')
                    break;
                case 'Foriegn Foods':
                    result = this.$t('foriegn_fd')
                    break;
                case 'BBQ':
                    result = this.$t('bbq')
                    break;
                case 'Brunch':
                    result = this.$t('brunch')
                    break;
                case 'Hot Pot':
                    result = this.$t('hotpot')
                    break;
            }
            return result;
        },
        handle_get_brands() {
            this.$store.dispatch('handle_get_brands').then (result => {
                this.brands = result.status == 200 ? result.data : []
            })
        },
        handle_change() {
            if(this.url_preview) {
                socket_bus.$emit('SHOW_MESSAGE_UPLOAD_IMAGE', {message: 'You want to save image ?'})
            }
        },
        handle_add_brand() {
            this.is_add = true;
        },
        handle_edit(brand) {
           socket_bus.$emit('EDIT_BRAND', {data: brand })
           console.log('brand');
        },
        handle_delete_brand(brand) {
            socket_bus.$emit('DELETE_BRAND', {data: brand })
        },
        handle_update_image(brand) {
            this.brand_selected = brand
        },
        handle_delete_link(index) {
             if(this.is_logo == 'MAIN_BANNER' || this.is_logo == 'IMAGE_INTRO' || this.is_logo == 'MENU') {
                this.url_preview = null,
                this.main_banner = ''
                this.image_intro = ''
                this.$refs.logo = []
                this.menu = ''
            } else {
                this.url_preview = null,
                this.logo = ''
                this.qr_code_image = ''
                this.thumbnail = ''
                this.$refs.logo = []
            }
        },
        handleFileUpload(e) {
            this.url_preview = []
            var files = e.target.files || e.dataTransfer.files;
            var _URL = window.URL || window.webkitURL;
             var image = new Image()
            if (!files.length)
                return;
            this.qr_code_image = files[0]
            this.logo = files[0],
            this.images = files[0];
            this.thumbnail = files[0]
            this.main_banner = files
            this.image_intro = files
            this.menu = files

            if(this.main_banner.length > 2) {
                socket_bus.$emit('SHOW_MESSAGE', {message: 'Main Banner Only 2 Picture'})
                this.main_banner = []
                e.target.value = ''
            } else if(this.image_intro.length > 5) {
                socket_bus.$emit('SHOW_MESSAGE', {message: 'Image Intro Only 5 Picture'})
                this.image_intro = []
                e.target.value = ''
            } else if(this.menu.length > 5) {
                socket_bus.$emit('SHOW_MESSAGE', {message: 'Menu Only 5 Picture'})
                this.menu = []
                e.target.value = ''
            }

            switch(this.is_logo) {
                case 'LOGO':
                    image.src = _URL.createObjectURL(files[0]);
                    image.onload = () => {
                        if(image.width != 270 || image.height != 270 ) {
                            socket_bus.$emit('SHOW_MESSAGE', {message: 'Size image upload is over rules'})
                            this.logo = null
                            e.target.value = ''
                        } else {
                            this.url_preview.push(URL.createObjectURL(files[0]));
                        }
                    }
                    break;
                case 'THUMBNAIL':
                    image.src = _URL.createObjectURL(files[0]);
                    image.onload = () => {
                        if(image.width != 870 || image.height != 270 ) {
                            socket_bus.$emit('SHOW_MESSAGE', {message: 'Size image upload is over rules'})
                            this.thumbnail = null
                            e.target.value = ''
                        } else {
                            this.url_preview.push(URL.createObjectURL(files[0]));
                        }
                    }
                    break;
                case 'MAIN_BANNER':
                    for(var i = 0; i < files.length; i++) {
                        image.src = _URL.createObjectURL(files[i]);
                        image.onload = () => {
                            if(image.width != 960 || image.height != 720 ) {
                                socket_bus.$emit('SHOW_MESSAGE', {message: 'Size image upload is over rules'})
                                this.main_banner = null
                                e.target.value = ''
                                this.url_preview = null
                            } else {
                                for(i = 0; i < files.length; i++) {
                                    this.url_preview.push(URL.createObjectURL(files[i]));
                                }
                            }
                        }
                    }
                    break;
                case 'IMAGE_INTRO':
                    for(i = 0; i < files.length; i++) {
                        image.src = _URL.createObjectURL(files[i]);
                        image.onload = () => {
                            if(image.width != 960 || image.height != 720 ) {
                                socket_bus.$emit('SHOW_MESSAGE', {message: 'Size image upload is over rules'})
                                this.image_intro = null
                                e.target.value = ''
                                this.url_preview = null
                            } else {
                               for(i = 0; i < files.length; i++) {
                                    this.url_preview.push(URL.createObjectURL(files[i]));
                                }
                            }
                        }
                    }
                    break;
                case 'MENU':
                    for(i = 0; i < files.length; i++) {
                        image.src = _URL.createObjectURL(files[i]);
                        image.onload = () => {
                            if(image.width != 960 || image.height != 720 ) {
                                socket_bus.$emit('SHOW_MESSAGE', {message: 'Size image upload is over rules'})
                                this.image_intro = null
                                e.target.value = ''
                                this.url_preview = null
                            } else {
                               for(i = 0; i < files.length; i++) {
                                    this.url_preview.push(URL.createObjectURL(files[i]));
                                }
                            }
                        }
                    }
                    break;
                case 'QR_CODE':
                    image.src = _URL.createObjectURL(files[0]);
                    image.onload = () => {
                        if(image.width != 90 || image.height != 90 ) {
                            socket_bus.$emit('SHOW_MESSAGE', {message: 'Size image upload is over rules'})
                            this.logo = null
                            e.target.value = ''
                        } else {
                            this.url_preview.push(URL.createObjectURL(files[0]));
                        }
                    }
                    break;
            }

        },
        handle_upload() {
            switch(this.is_logo) {
                case 'LOGO':
                    this.handle_up_logo();
                    break;
                case 'THUMBNAIL':
                    this.handle_up_thumbnail();
                    break;
                case 'MAIN_BANNER':
                    this.handle_up_main_banner();
                    break;
                case 'IMAGE_INTRO':
                    this.handle_up_image_intro();
                    break;
                case 'MENU':
                    this.handle_up_menu();
                    break;
                case 'QR_CODE':
                    this.handle_up_qr_code_image();
                    break;
            }
        },
        handle_up_logo() {
            const data_form = new FormData()
            data_form.append('name', this.brand_selected.id)
            data_form.append('logo', this.logo);

            return new Promise((resolve, reject) => {
                HTTP.post('/brands/upload-logo/' + this.brand_selected.id, data_form, {
                    headers: {
                        'Content-Type' : 'multipart/form-data'
                    }
                }).then(result => {
                    resolve(result)
                    if(result.data.status == 200) {
                        setTimeout(() => {window.location.reload()}, 3000) 
                        socket_bus.$emit('SHOW_MESSAGE',{message: 'Update Image Success !!!'})
                    } else {
                        socket_bus.$emit('SHOW_MESSAGE',{message: 'Update Image Fail !!!'})
                    }
                }).catch(error => {
                    reject(error)
                    console.log('Error: ' + error);
                })
            })
        },
        handle_up_thumbnail() {
            const data_form = new FormData();
            data_form.append("name", this.brand_selected.id)
            data_form.append("thumbnail", this.thumbnail)

            return new Promise((resolve, reject) => {
                HTTP.post('/brands/upload-thumbnail/' + this.brand_selected.id, data_form, {
                    headers: {
                        'Content-Type' : 'multipart/form-data'
                    }
                }).then(result => {
                    resolve(result)
                    if(result.data.status == 200) {
                        window.location.reload()
                        alert('Update Image Success !!!')
                    } else {
                        alert('Update Image Fail !!!')
                    }
                }).catch(error => {
                    reject(error)
                    console.log('Error: ' + error);
                })
            })
        },
        handle_up_qr_code_image() {
            const data_form = new FormData();
            data_form.append("name", this.brand_selected.id)
            data_form.append("qr_code_image", this.thumbnail)

            return new Promise((resolve, reject) => {
                HTTP.post('/brands/upload-qr-code/' + this.brand_selected.id, data_form, {
                    headers: {
                        'Content-Type' : 'multipart/form-data'
                    }
                }).then(result => {
                    resolve(result)
                    if(result.data.status == 200) {
                        window.location.reload()
                        alert('Update Qr Code Success !!!')
                    } else {
                        alert('Update Qr Code Fail !!!')
                    }
                }).catch(error => {
                    reject(error)
                    console.log('Error: ' + error);
                })
            })
        },
        handle_up_main_banner() {
            const data_form = new FormData();
            data_form.append("name", this.brand_selected.id)
            for(let i = 0; i < this.main_banner.length; i++) {
                data_form.append('main_banner', this.main_banner[i])
            }

            return new Promise((resolve, reject) => {
                HTTP.post('/brands/upload-banners/' + this.brand_selected.id, data_form, {
                    headers: {
                         'Content-Type' : 'multipart/form-data'
                    }
                }).then(result => {
                    resolve(result)
                    if(result.data.status == 200) {
                        window.location.reload()
                        alert('Update Image Success !!!')
                    } else {
                        alert('Update Image Fail !!!')
                    }
                }).catch(error => {
                    reject(error)
                    console.log('Error: ' + error);
                })
            })
        },
        handle_up_image_intro() {
            const data_form = new FormData();
            data_form.append("name", this.brand_selected.id)
            for(let i = 0; i < this.image_intro.length; i++) {
                data_form.append('image_intro', this.image_intro[i])
            }

            return new Promise((resolve, reject) => {
                HTTP.post('/brands/upload-intro-image/' + this.brand_selected.id, data_form, {
                    headers: {
                         'Content-Type' : 'multipart/form-data'
                    }
                }).then(result => {
                    resolve(result)
                    if(result.data.status == 200) {
                        window.location.reload()
                        alert('Update Image Success !!!')
                    } else {
                        alert('Update Image Fail !!!')
                    }
                }).catch(error => {
                    reject(error)
                    console.log('Error: ' + error);
                })
            })
        },
        handle_up_menu() {
            const data_form = new FormData();
            data_form.append("name", this.brand_selected.id)
            for(let i = 0; i < this.menu.length; i++) {
                data_form.append('menu', this.menu[i])
            }

            return new Promise((resolve, reject) => {
                HTTP.post('/brands/upload-menu/' + this.brand_selected.id, data_form, {
                    headers: {
                        'Content-Type' : 'multipart/form-data'
                    }
                }).then(result => {
                    resolve(result)
                    if(result.data.status == 200) {
                       this.brand_selected = null
                       socket_bus.$emit('SHOW_MESSAGE',{message: 'Update Image Success !!!'})
                        setTimeout(() => {window.location.reload()}, 3000) 

                    } else {
                        socket_bus.$emit('SHOW_MESSAGE',{message: 'Update Image Fail !!!'})
                    }
                }).catch(error => {
                    reject(error)
                    console.log('Error: ' + error);
                })
            })
        },
        handle_close_form_up() {
            this.is_logo = null
            this.brand_selected = null
            this.logo = null
            this.main_banner = null
            this.image_intro = null
            this.thumbnail = null
            this.url_preview = null
        }
    },
    created() {
        this.handle_get_brands()

        socket_bus.$on('CANCEL_ADD_BRAND', result => {
            this.is_add = result.state
        })

        socket_bus.$on("UPDATE_LIST_BRAND", result => {
            this.brands.push(result.data)
        })

        socket_bus.$on('RESPONSE_DELETE', result => {
            if(result.is_delete) {
                this.brands.splice(this.brands.indexOf(result.data), 1)
                HTTP.delete('/brands/' + result.data.id).then(result => {
                    if(result.data.status == 200) {
                        socket_bus.$emit('SHOW_MESSAGE',{message: 'Delete Brand Success !!!'})
                    } else {
                        socket_bus.$emit('SHOW_MESSAGE',{message: 'Delete Brand Fail !!!'})
                    }
                })
                .catch(error => {
                    console.log('Error Delete: ' + error);
                })
            }
        })

        socket_bus.$on('CLOSE_BOX', result => {
            this.handle_get_brands()
        })

        socket_bus.$on('RESULT_UPLOAD_IMAGE', result => {
            if(result.is_save) {
                this.handle_upload()
            } else {
                this.url_preview = null
            }
        })
    }   
}
